import React, { Component } from 'react';
import './App.css';
import Numbers from './components/numbers';
import Header from './components/header';
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSync } from '@fortawesome/free-solid-svg-icons'

library.add(faSync)

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      options: [ 1, 2, 3, 4, 5, 6, 7, 8, 9 ],
      attempts: 5,
      numbersClicked: []
    };
  }

  render() {
    const divClass = { display: 'flex', justifyContent: 'space-around', width: '100%', marginBottom: '10px' };
    const equalsButton = { backgroundColor: 'lightgrey', borderRadius: '3px', cursor: 'pointer' };
    const retryButton = { backgroundColor: 'orange', border: 'none', borderRadius: '5px', padding: '5px 10px', color: 'white', cursor: 'pointer' };
    return (
      <div className="container">
        <Header />
        <div className="row">
          <div className="col-md-5" style={ divClass }>
            { Math.floor(Math.random() * 9) + 1 }
          </div>
          <div className="col-md-2">
            <button style={ equalsButton }> = </button>
          </div>
          <div className="col-md-5" style={ divClass }>
            <button style={ retryButton }><FontAwesomeIcon icon="sync" /> { this.state.attempts }</button>
          </div>
        </div>
        <Numbers className="col-md-5" options={this.state.options} handleClick={this.onClickedButton} />
      </div>
    );
  }

  onClickedButton = (number) => {
    let newNumbersClicked;
    if (this.state.numbersClicked.indexOf(number) < 0) {
      newNumbersClicked = [ ...this.state.numbersClicked, number ];
    } else {
      newNumbersClicked = this.state.numbersClicked.filter(n => n !== number);
    }
    this.setState((prevState) => {
      return {
        options: prevState.options,
        attempts: prevState.attempts,
        numbersClicked: newNumbersClicked
      }
    });
  }
}

export default App;
