import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      options: [ 1, 2, 3, 4, 5, 6, 7, 8, 9 ],
      attempts: []
    };
  }

  render() {
    const divClass = { display: 'flex', justifyContent: 'space-around', width: '100%', marginBottom: '10px' };
    const divButtons = { display: 'flex', justifyContent: 'space-around', width: '60%', margin: '0 20%',
      border: '1px solid lightgrey', 'padding': '5px 15px', boxSizing: 'border-box', borderRadius: '5px', cursor: 'pointer' };
    const roundButton = { borderRadius: '50%', height: '30px', width: '30px', color: 'black',
      backgroundColor: 'lightgrey', border: 'none', cursor: 'pointer' };
    const equalsButton = { backgroundColor: 'lightgrey', borderRadius: '3px', cursor: 'pointer' };
    const retryButton = { backgroundColor: 'orange', border: 'none', borderRadius: '5px', padding: '5px 10px',
      color: 'white', cursor: 'pointer' };
    return (
      <div>
        <h2>Play Nine</h2>
        <hr/>
        <div style={ divClass }>
          { Math.floor(Math.random() * 9) + 1 }
          <button style={ equalsButton }> = </button>
          <div></div>
        </div>
        <div style={ divClass }>
          <button style={ retryButton }>
            Retry {this.state.attempts}
          </button>
        </div>
        <div style={ divButtons }>
          { this.state.options.map(opt => <button style={ roundButton }>{ opt }</button>) }
        </div>
      </div>
    );
  }
}

export default App;
