import React, { Component } from 'react';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      options: [ 1, 2, 3, 4, 5, 6, 7, 8, 9 ],
      attempts: []
    };
  }

  render() {
    const divClass = { display: 'flex', justifyContent: 'space-around', width: '100%', marginBottom: '5px' };
    return (
      <div>
        Play Nine
        <hr/>
        <div style={ divClass }>
          { Math.floor(Math.random() * 9) + 1 }
          <button style={ {backgroundColor: 'lightgrey', borderRadius: '3px'} }>=</button>
          <div></div>
        </div>
        <div style={ divClass }>
          <button>retry {this.state.attempts}</button>
        </div>
        <div style={ {margin: '0 5%', width: '90%'} }>
          { this.state.options.map(opt => <button>{ opt }</button>) }
        </div>
      </div>
    );
  }
}

export default App;
